sys_script_include.script:
var GetEmailAddress = Class.create();
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getEmail: function(){
		var data = '';
		try{
		gs.info('99999999999999999999999---------------------------');
		var userRecord = new GlideRecord("sys_user");

		userRecord.get(this.getParameter('sysparm_userID'));
		var myObj = {};
		myObj.firstname = userRecord.first_name + '';
		myObj.lastname = userRecord.last_name + '';
		myObj.email = userRecord.email + '';

		var json = new global.JSON();
		gs.info(myObj.email);
		data = json.encode(myObj);

		gs.info(data);
		gs.info(myObj.email);
		gs.info('--------end--------');
		}catch (err){
			gs.info(err.message);
		}
		return data;
		//return userRecord.email + '';
	},

    type: 'GetEmailAddress'
});
